import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RestaurantPage.css';

interface MenuItem {
  _id: string;
  name: string;
  price: number;
  description: string;
}

interface RestaurantData {
  name: string;
  description: string;
  menu: MenuItem[];
}

interface RestaurantPageProps {
    restaurantId: string;
}

const RestaurantPage: React.FC<RestaurantPageProps> = ({ restaurantId }) => {
  const [restaurantData, setRestaurantData] = useState<RestaurantData | null>(null);

  useEffect(() => {
    axios.get(`http://localhost:3001/api/restaurant/get/${restaurantId}`) // Replace with your actual API endpoint
      .then(response => {
        setRestaurantData(response.data);
      })
      .catch(error => {
        console.error('Error fetching restaurant data:', error);
      });
  }, [restaurantId]);

  if (!restaurantData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="restaurant-page">
      <h2 className="restaurant-name">{restaurantData.name}</h2>
      <p className="restaurant-description">{restaurantData.description}</p>
      <h3>Menu</h3>
      <ul className="menu-list">
        {restaurantData.menu.map(item => (
          <li key={item._id}>
            <h4 className="menu-item-name">{item.name}</h4>
            <p className="menu-item-description">{item.description}</p>
            <p className="menu-item-price">${item.price.toFixed(2)}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RestaurantPage;
